static int
check_tz ()
{
    int retcode = 0 ;
    char *tz ; 
    tz = getenv("TZ") ; 
    strcpy(OldTZ+3, tz) ; 
    putenv ( ETimezone ) ; 
    tz = getenv("TZ") ; 
    errno = 0 ;
    tzset() ; 
    /* show_tz("str2epoch: check_tz" ) ; */
    if ( errno != 0 ) {
	errno = 0 ;
        retcode = -1 ;
    }
    return retcode ;
}

int zstr2epoch ( char *s, double *e ) 
{ 
    Cp = s ; 
    Retcode = 0 ;

    *Timezone = 0 ; 
    Year = 1970 ;
    Month = -1;
    Day = -1;
    Hour = 0;
    Minute = 0;
    Doy = 0;
    Num = 0;
    Second = 0;		
    Etime = 0.0;
    Am = - 1 ; 

    EP_lex () ; 
    EP_restart(stdin) ; 

    if ( Month < 1 ) Month = 1 ; 
    if ( Day < 1 ) Day = 1 ; 
    if ( Am == 1 && Hour == 12) Hour -= 12; /* allow 12:30 am to mean 0:30 am */
    if ( Am == 0 && Hour <  12) Hour += 12;

    if ( Etime == 0.0 ) {
	if (Doy == 0) {
	    Doy = mday2doy(Year, Month, Day) ;
	}
	if ( Year < 100 ) Year += 1900 ; 
	Etime = h2e ( Year, Doy, Hour, Minute, Second ) ; 
    }

    if (*Timezone != 0 ) {
	    struct tm utm ; 
	    time_t utime ; 
	    int year, doy, hour, minute, month, mday ; 
	    double second, msec ;
	    /* don't have to run tzset, because it was already done in check_tz above */
	    e2h ( Etime, &year, &doy, &hour, &minute, &second ) ; 
	    msec = Etime - floor(Etime) ;
	    doy2mday ( doy, year, &month, &mday ) ; 
	    utm.tm_sec = second ; 
	    utm.tm_min = minute ; 
	    utm.tm_hour = hour ; 
	    utm.tm_mon = month-1 ;
	    utm.tm_mday = mday ; 
	    utm.tm_year = year - 1900 ; 
	    utm.tm_isdst = -1 ;
	    utime = mktime ( &utm ) ; 
	    if ( utime == -1 ) {
		register_error ( 0, "Can't convert to local timezone before 1970\n" ) ; 
		Retcode++ ;
	    }
	    Etime = ((double) utime) + msec ; 
	    /* now restore the original timezone */
	    putenv ( OldTZ ) ; 
	    tzset() ; 
	    /* show_tz ( "str2epoch: restoring" ) ; */
    }

    *e = Etime ; 

    return Retcode ;
}


double str2epoch ( s ) 
char *s ; 
{ 
    double e ; 

    zstr2epoch ( s, &e ) ;

    return e ;
}

static int my_yyinput ( buf, ms ) 
char *buf ; 
int ms ; 
{
    int n ; 

    n = MIN ( ms, strlen(Cp)) ; 
    memcpy ( buf, Cp, n ) ; 
    Cp += n ; 
    return n ; 
}

